AJS.toInit(function(){var b=AJS.$("#baseURL").val();function c(){AJS.$.ajax({url:b+"/rest/dealertrack/change/1.0/LDAPConfigResource/ldapDetails",type:"GET",contentType:"application/json",success:function(d){AJS.$("#ldapinitctx").attr("value",d.ldapInitCtx);AJS.$("#ldapsrvrname").attr("value",d.ldapSrvrName);AJS.$("#ldapbasedn").attr("value",d.ldapBaseDn);AJS.$("#ldapuid").attr("value",d.ldapUid);AJS.$("#ldappwd").attr("value",d.ldapPwd)}})}function a(){AJS.$.ajax({url:b+"/rest/dealertrack/change/1.0/LDAPConfigResource/ldapDetailsave",type:"PUT",contentType:"application/json",data:'{ "ldapInitCtx": "'+AJS.$("#ldapinitctx").attr("value")+'","ldapSrvrName": "'+AJS.$("#ldapsrvrname").attr("value")+'", "ldapBaseDn": "'+AJS.$("#ldapbasedn").attr("value")+'", "ldapUid": "'+AJS.$("#ldapuid").attr("value")+'", "ldapPwd": "'+AJS.$("#ldappwd").attr("value")+'" }',processData:false,success:function(){JIRA.Messages.showSuccessMsg("ldap Plugin Configuration Saved Successfully!")},error:function(f,d,e){JIRA.Messages.showErrorMsg("LDAP Plugin Configuration could not be saved!<br><b>Server returned the following Error:</b><br> "+f.responseText)}})}c();AJS.$("#ldapConfigSave").live("click",function(d){a()})});