AJS.toInit(function(){var b=AJS.$("#baseURL").val();function c(){AJS.$.ajax({url:b+"/rest/cabsummary/1.0/emailConfigResource/emailConfig",type:"GET",contentType:"application/json",success:function(d){AJS.$("#userName").attr("value",d.userName);AJS.$("#password").attr("value",d.password);AJS.$("#feilds").attr("value",d.feilds)}})}function a(){AJS.$.ajax({url:b+"/rest/cabsummary/1.0/emailConfigResource/emailConfigSave",type:"PUT",contentType:"application/json",data:'{ "userName": "'+AJS.$("#userName").attr("value")+'","password": "'+AJS.$("#password").attr("value")+'", "feilds": "'+AJS.$("#feilds").attr("value")+'" }',processData:false,success:function(){JIRA.Messages.showSuccessMsg("ldap Plugin Configuration Saved Successfully!")},error:function(f,d,e){JIRA.Messages.showErrorMsg(" Configuration Details could not be saved!<br><b>Server returned the following Error:</b><br> "+f.responseText)}})}c();AJS.$("#emailConfigSave").live("click",function(d){a()})});