var RoleBasedMapper={addRoleBasedMapping:function(){if(AJS.$("#selectedIssueTypes").val()!==""&&AJS.$("#selectedProjectRoles").val()!==""){var b=AJS.$("#selectedSolutionGroups").val();var a=AJS.$("#selectedProjectRoles").val();if(b.indexOf("&")>0){b=b.replace(/&/g,"%26")}if(a.indexOf("&")>0){a=a.replace(/&/g,"%26")}console.log("solutionGroupVal:"+b);AJS.$.ajax({url:"/rest/rolebasedmap/1.0/ProjectRoles/addRoleBasedMap.json?projectKey="+AJS.$("#selectedProjects").val()+"&issueType="+AJS.$("#selectedIssueTypes").val()+"&solutionGroup="+b+"&projectRole="+a,type:"PUT",contentType:"application/json",success:function(c){console.log("add mapping:"+c.length);AJS.$("#rolebasedmaptable tbody tr").remove();RoleBasedTableRowAppender.showTableRows(c);console.log("mapping added");JIRA.Messages.showSuccessMsg("FieldMapping Mapping Added!")},error:function(c){JIRA.Messages.showErrorMsg("Duplicate Entry Exist for "+AJS.$("#selectedSolutionGroups").val())}})}},removeRoleBasedMapping:function(b,a,c,d){if(c.indexOf("&")>0){c=c.replace(/&/g,"%26")}if(d.indexOf("&")>0){d=d.replace(/&/g,"%26")}AJS.$.ajax({url:"/rest/rolebasedmap/1.0/ProjectRoles/deleteRoleBasedMap.json?projectKey="+b+"&issueType="+a+"&solutionGroup="+c+"&projectRole="+d,type:"PUT",contentType:"application/json",success:function(e){console.log("delete mapping:"+e.length);AJS.$("#rolebasedmaptable tbody tr").remove();RoleBasedTableRowAppender.showTableRows(e);console.log("mapping deleted")}});JIRA.Messages.showSuccessMsg("FieldMapping Mapping Deleted!")},getAllRoleBasedMappings:function(){AJS.$("#rolebasedmaptable tbody tr").remove();AJS.$.ajax({url:"/rest/rolebasedmap/1.0/ProjectRoles/getAllRoleBasedMap",dataType:"json",type:"PUT",success:function(a){console.log("Pre Fetching mapping:"+a.length);AJS.log("Parsing the response and converting them to table rows");RoleBasedTableRowAppender.showTableRows(a);console.log("pre mapping fetched")}})}};