var Mapper={addMapping:function(){console.log("selectedProjectIssueTypes:"+AJS.$("#selectedProjectIssueTypes").val());console.log("selectedIssueTypes:"+AJS.$("#selectedIssueTypes").val());console.log("selectedDefaultIssueFields:"+AJS.$("#selectedDefaultIssueFields").val());console.log("mappingfield:"+AJS.$("#mappingfield").val());if(AJS.$("#selectedDefaultIssueFields").val()!==""&&AJS.$("#mappingfield").val()!==""){AJS.$.ajax({url:"/rest/dt/fieldsmap/1.0/IssueFieldMap/addIssueFieldMapping.json?jiraField="+AJS.$("#selectedDefaultIssueFields").val()+"&mappingField="+AJS.$("#mappingfield").val()+"&fromIssueType="+AJS.$("#selectedProjectIssueTypes").val()+"&toIssueType="+AJS.$("#selectedIssueTypes").val(),type:"PUT",contentType:"application/json",success:function(a){console.log("add mapping:"+a.length);AJS.$("#issuemappingstable tbody tr").remove();TableRowAppender.showTableRows(a);console.log("mapping added")}});JIRA.Messages.showSuccessMsg("FieldMapping Mapping Added!")}},removeMapping:function(d,b,c,a){AJS.$.ajax({url:"/rest/dt/fieldsmap/1.0/IssueFieldMap/deleteIssueFieldMapping.json?jiraField="+d+"&mappingField="+b+"&fromIssueType="+c+"&toIssueType="+a,type:"PUT",contentType:"application/json",success:function(e){console.log("delete mapping:"+e.length);AJS.$("#issuemappingstable tbody tr").remove();TableRowAppender.showTableRows(e);console.log("mapping deleted")}});JIRA.Messages.showSuccessMsg("FieldMapping Mapping Deleted!")},getAllFieldMappings:function(){AJS.$("#issuemappingstable tbody tr").remove();AJS.$.ajax({url:"/rest/dt/fieldsmap/1.0/IssueFieldMap/getAllIssueFieldMappings",dataType:"json",type:"PUT",success:function(a){console.log("Pre Fetching mapping:"+a.length);AJS.log("Parsing the response and converting them to table rows");TableRowAppender.showTableRows(a);console.log("pre mapping fetched")}})},};